                    while ( _x > 0 )
                    {
                        _verticalLines.push ( new Line ( { x: _x, y: 0 }, { x: _x, y: _grid.clientHeight } ) );

                        _x = _x - _multiplier;
                    }


                        _x = _center.x + _multiplier;

                    while ( _x < _grid.width )
                    {
                        _verticalLines.push ( new Line ( { x: _x, y: 0 }, { x: _x, y: _grid.clientHeight } ) );

                        _x = _x + _multiplier;
                    }

