                    _centerLines.draw ( 'grid' );


                    _horizontalLines.stroke.color = new Rgb ( 0, 0, 0, 0.25 );

                    _horizontalLines.draw ( 'grid' );


                    _verticalLines.stroke.color = new Rgb ( 0, 0, 0, 0.25 );

                    _verticalLines.draw ( 'grid' );
